contracts/Vesting/TokenVesting.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import { Errors } from "./../libraries/Errors.sol";

contract TokenVesting is Ownable, ReentrancyGuard, Pausable {
    using SafeERC20 for IERC20;

    event CreatedVestingSchedule(address user, bytes32 scheduleId);

    /// <=============== STATE VARIABLES ===============>

    /// Blast TOKEN
    IERC20 public blastToken;

    struct VestingSchedule {
        // beneficiary of tokens after they are released
        address beneficiary;
        // start time of the vesting period
        uint256 start;
        // cliffStart time in seconds
        uint256 cliffStart;
        // duration of the vesting period in seconds
        uint256 duration;
        // the amount that is immediately vested at grant
        uint256 immediateVestedAmount;
        // total amount of tokens to be released at the end of the vesting EXCLUDING immediateVestedAmount
        uint256 amountTotal;
        // amount of tokens released
        uint256 released;
        // whether or not the vesting is revocable
        bool revocable;
        // whether or not the vesting has been revoked
        bool revoked;
    }

    bytes32[] private vestingSchedulesIds;
    mapping(bytes32 => VestingSchedule) private vestingSchedules;
    uint256 private vestingSchedulesTotalAmount;
    mapping(address => uint256) private holdersVestingCount;

    event Released(
        address beneficiary,
        bytes32 vestingScheduleId,
        uint256 amount,
        uint256 releaseTimestamp
    );
    event Revoked(bytes32 vestingScheduleId, uint256 revokeTimestamp);

    constructor(IERC20 _blastToken) {
        blastToken = _blastToken;
    }

    /// <=============== MUTATIVE METHODS ===============>

    /// @notice Creates a new vesting schedule for a beneficiary
    function createVestingSchedule(
        address _beneficiary,
        uint256 _start,
        uint256 _cliffDuration,
        uint256 _duration,
        uint256 _immediateReleaseAmount,
        uint256 _amountTotal,
        bool _revocable
    ) external whenNotPaused onlyOwner {
        require(_beneficiary != address(0), Errors.NO_ZERO_ADDRESS);
        require(getWithdrawableAmount() >= (_amountTotal + _immediateReleaseAmount), Errors.INSUFFICIENT_TOKENS);
        require(_duration > 0, Errors.DURATION_INVALID);
        require(_amountTotal > 0, Errors.INVALID_AMOUNT);
        require(_start > block.timestamp, Errors.START_TIME_INVALID);
        require(_cliffDuration <= _duration, Errors.DURATION_INVALID);

        bytes32 vestingScheduleId = computeNextVestingScheduleIdForHolder(
            _beneficiary
        );
        uint256 cliff = _start + _cliffDuration;
        vestingSchedules[vestingScheduleId] = VestingSchedule({
            beneficiary: _beneficiary,
            start: _start,
            cliffStart: cliff,
            duration: _duration,
            immediateVestedAmount: _immediateReleaseAmount,
            amountTotal: _amountTotal,
            released: 0,
            revocable: _revocable,
            revoked: false
        });
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount + _amountTotal + _immediateReleaseAmount;
        vestingSchedulesIds.push(vestingScheduleId);
        uint256 currentVestingCount = holdersVestingCount[_beneficiary];
        holdersVestingCount[_beneficiary] = currentVestingCount + 1;

        emit CreatedVestingSchedule(_beneficiary, vestingScheduleId);
    }

    /**
     * @notice Revokes the vesting schedule for given identifier.
     * @param vestingScheduleId the vesting schedule identifier
     */
    function revoke(bytes32 vestingScheduleId) external whenNotPaused onlyOwner {
        VestingSchedule storage vestingSchedule = vestingSchedules[vestingScheduleId];
        require(!vestingSchedule.revoked, Errors.SCHEDULE_REVOKED);
        require(vestingSchedule.revocable, Errors.NOT_REVOCABLE);
        uint256 releasableAmount = _computeReleasableAmount(vestingSchedule);
        if (releasableAmount > 0) {
            release(vestingScheduleId, releasableAmount);
        }
        uint256 unreleased = vestingSchedule.amountTotal -
            vestingSchedule.released;
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - unreleased;
        vestingSchedule.revoked = true;
        blastToken.safeTransfer(owner(), unreleased);

        emit Revoked(vestingScheduleId, block.timestamp);
    }

    /// @notice Release vested amount of tokens.
    function release(bytes32 vestingScheduleId, uint256 amount)
        public
        whenNotPaused
        nonReentrant
    {
        VestingSchedule storage vestingSchedule = vestingSchedules[vestingScheduleId];
        require(!vestingSchedule.revoked, Errors.SCHEDULE_REVOKED);
        address beneficiary = vestingSchedule.beneficiary;
        bool isBeneficiary = _msgSender() == beneficiary;
        bool isOwner = _msgSender() == owner();
        require(isBeneficiary || isOwner, Errors.BENEFICIARY_OR_OWNER);
        uint256 releasableAmount = _computeReleasableAmount(vestingSchedule);
        require(releasableAmount >= amount, Errors.NOT_ENOUGH_TOKENS);
        vestingSchedule.released = vestingSchedule.released + amount;
        vestingSchedulesTotalAmount = vestingSchedulesTotalAmount - amount;
        blastToken.safeTransfer(beneficiary, amount);
        emit Released(_msgSender(), vestingScheduleId, amount, block.timestamp);
    }

    /// <=============== VIEWS ===============>

    /**
     * @dev Returns the amount of tokens that can be withdrawn by the owner.
     * @return the amount of tokens
     */
    function getWithdrawableAmount() public view returns (uint256) {
        return
            blastToken.balanceOf(address(this)) - vestingSchedulesTotalAmount;
    }

    /**
     * @dev Computes the next vesting schedule identifier for a given holder address.
     */
    function computeNextVestingScheduleIdForHolder(address holder)
        public
        view
        returns (bytes32)
    {
        return
            computeVestingScheduleIdForAddressAndIndex(
                holder,
                holdersVestingCount[holder]
            );
    }

    /**
     * @dev Computes the vesting schedule identifier for an address and an index.
     */
    function computeVestingScheduleIdForAddressAndIndex(
        address holder,
        uint256 index
    ) public pure returns (bytes32) {
        return keccak256(abi.encodePacked(holder, index));
    }

    /**
     * @notice Computes the vested amount of tokens for the given vesting schedule identifier.
     * @return the vested amount
     */
    function computeReleasableAmount(bytes32 vestingScheduleId)
        external
        view
        returns (uint256)
    {
        require(!vestingSchedules[vestingScheduleId].revoked, Errors.SCHEDULE_REVOKED);
        VestingSchedule storage vestingSchedule = vestingSchedules[
            vestingScheduleId
        ];
        return _computeReleasableAmount(vestingSchedule);
    }

    /**
     * @dev Computes the releasable amount of tokens for a vesting schedule.
     * @return the amount of releasable tokens
     */
    function _computeReleasableAmount(VestingSchedule memory vestingSchedule)
        internal
        view
        returns (uint256)
    {
        uint256 currentTime = block.timestamp;
        if (currentTime < vestingSchedule.cliffStart) {
            return vestingSchedule.immediateVestedAmount - vestingSchedule.released;
        } else if (
            currentTime >= vestingSchedule.cliffStart + vestingSchedule.duration
        ) {
            return
                vestingSchedule.amountTotal +
                vestingSchedule.immediateVestedAmount -
                vestingSchedule.released;
        } else {
            uint256 timeFromStart = currentTime - vestingSchedule.cliffStart;
            uint256 vestedAmount = vestingSchedule.amountTotal * timeFromStart / vestingSchedule.duration;
            vestedAmount = vestedAmount + vestingSchedule.immediateVestedAmount - vestingSchedule.released;
            return vestedAmount;
        }
    }

    /**
     * @notice Returns the vesting schedule information for a given identifier.
     * @return the vesting schedule structure information
     */
    function getVestingSchedule(bytes32 vestingScheduleId)
        public
        view
        returns (VestingSchedule memory)
    {
        return vestingSchedules[vestingScheduleId];
    }

    /**
     * @notice Returns the vesting schedule information for a given holder and index.
     * @return the vesting schedule structure information
     */
    function getVestingScheduleByAddressAndIndex(address holder, uint256 index)
        external
        view
        returns (VestingSchedule memory)
    {
        return
            getVestingSchedule(
                computeVestingScheduleIdForAddressAndIndex(holder, index)
            );
    }

    function pause(bool stop) external onlyOwner {
        if (stop) {
            _pause();
        } else {
            _unpause();
        }
    }
}

contracts/Utilities/Upgrader.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/ERC20Burnable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./../interfaces/IBlastEquipmentNFT.sol";
import "./Utility.sol";

contract Upgrader is Utility {
    using SafeERC20 for IERC20;

    event LevelUpgraded(uint256 tokenId, address owner, uint256 newLevel);

    // Calculation related variables
    struct Attributes {
        uint16[10] pricePerRarity;
        uint16[10] pricePerAdjective;
        uint256 pricePerLevel; // decimal factor 100000
    }

    uint256 public durabilityEffectDivider = 48;
    uint256[10] public maxLevelPerRarity = [
        10, 12, 15, 17, 20,
        22, 25, 27, 30, 35
    ];
    uint16[5] public multiplierPerGrade = [1740, 1520, 1320, 1150, 1000];
    Attributes public bltAttribute;
    Attributes public maticAttribute;
    Attributes public csAttribute;

    constructor(
        IBlastEquipmentNFT _blastEquipmentNFT,
        IERC20 _blastToken,
        ERC20Burnable _csToken,
        address _treasuryAddress,
        address _companyAddress
    ) Utility(_blastEquipmentNFT, _blastToken, _csToken, _treasuryAddress, _companyAddress) {
        uint16[10] memory _bltPricePerRarity = [
            uint16(3), 4, 4, 5, 5,
            6, 7, 7, 8, 9
        ];
        uint16[10] memory _bltPricePerAdjective = [
            uint16(0), 0, 0, 1, 1,
            2, 2, 3, 4, 4
        ];
        uint16[10] memory _maticPricePerRarity = [
            uint16(30), 40, 40, 50, 50,
            60, 70, 70, 80, 90
        ];
        uint16[10] memory _maticPricePerAdjective = [
            uint16(0), 0, 0, 13, 13,
            27, 27, 40, 53, 53
        ];
        uint16[10] memory _csPricePerRarity = [
            100, 144, 207, 297, 427,
            613, 881, 1266, 1819, 2613
        ];
        uint16[10] memory _csPricePerAdjective = [
            0, 20, 50, 80, 80,
            180, 180, 330, 480, 480
        ];

        bltAttribute = Attributes({
            pricePerRarity: _bltPricePerRarity,
            pricePerAdjective: _bltPricePerAdjective,
            pricePerLevel: 500
        });
        maticAttribute = Attributes({
            pricePerRarity: _maticPricePerRarity, // DECIMAL 100
            pricePerAdjective: _maticPricePerAdjective, // DECIMAL 100
            pricePerLevel: 500
        });
        csAttribute = Attributes({
            pricePerRarity: _csPricePerRarity,
            pricePerAdjective: _csPricePerAdjective,
            pricePerLevel: 2500
        });
    }

    function setDurabilityEffectDivider(uint256 _newValue) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_newValue > 0, Errors.NO_ZERO_VALUE);
        durabilityEffectDivider = _newValue;
    }

    function setMultiplierPerGrade(uint16[5] memory _multiplierPerGrade) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_multiplierPerGrade.length == 5, Errors.INVALID_PARAM);
        for (uint8 i = 0; i < 5; i++) {
            multiplierPerGrade[i] = _multiplierPerGrade[i];
        }
    }

    function upgrade(uint256 _tokenId, uint256 _increaseLevel) external nonReentrant whenNotPaused {
        require(!isUsingMatic, Errors.USING_MATIC_NOW);
        require(_msgSender() == blastEquipmentNFT.ownerOf(_tokenId), Errors.NOT_OWNER);

        uint256 bltPrice = getMultipleRequiredPrice(0, _tokenId, _increaseLevel);
        uint256 csPrice = getMultipleRequiredPrice(1, _tokenId, _increaseLevel);
        (, , , , uint8 rarity, ) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);
        require(level != 0, Errors.INVALID_PARAM);
        require(level != maxLevelPerRarity[rarity], Errors.MAX_LEVEL_REACHED);
        require(bltPrice != 0 && csPrice != 0, Errors.INVALID_PARAM);

        csToken.burnFrom(_msgSender(), csPrice);
        blastToken.safeTransferFrom(
            _msgSender(),
            treasuryAddress,
            bltPrice / 4
        );
        blastToken.safeTransferFrom(
            _msgSender(),
            companyAddress,
            (bltPrice * 3) / 4
        );

        blastEquipmentNFT.setLevel(_tokenId, level + _increaseLevel);

        emit LevelUpgraded(_tokenId, _msgSender(), level + _increaseLevel);
    }

    function upgradeUsingMatic(uint256 _tokenId, uint256 _increaseLevel) external payable nonReentrant whenNotPaused {
        require(isUsingMatic, Errors.NOT_USING_MATIC_NOW);
        require(_msgSender() == blastEquipmentNFT.ownerOf(_tokenId), Errors.NOT_OWNER);

        uint256 bltPrice = getMultipleRequiredPrice(0, _tokenId, _increaseLevel);
        uint256 csPrice = getMultipleRequiredPrice(1, _tokenId, _increaseLevel);
        (, , , , uint8 rarity, ) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);
        require(level != 0, Errors.INVALID_PARAM);
        require(level != maxLevelPerRarity[rarity], Errors.MAX_LEVEL_REACHED);
        require(bltPrice != 0 && csPrice != 0, Errors.INVALID_PARAM);

        csToken.burnFrom(_msgSender(), csPrice);
        require(msg.value == bltPrice, Errors.INVALID_AMOUNT);
        (bool sent1, ) = payable(treasuryAddress).call{value: bltPrice / 4}("");
        require(sent1, Errors.FAILED_TO_SEND_ETHER_TREASURY);
        (bool sent2, ) = payable(companyAddress).call{value: (bltPrice * 3) / 4}("");
        require(sent2, Errors.FAILED_TO_SEND_ETHER_COMPANY);

        blastEquipmentNFT.setLevel(_tokenId, level + _increaseLevel);

        emit LevelUpgraded(_tokenId, _msgSender(), level + _increaseLevel);
    }

    function getPrice(uint8 _tokenType, uint8 maxDurability, uint8 adjective, uint8 rarity, uint8 grade, uint256 level) internal view returns (uint256) {
        if (_tokenType == 0) {
            if (isUsingMatic) {
                return (maticAttribute.pricePerRarity[rarity] + maticAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * maticAttribute.pricePerLevel) * multiplierPerGrade[grade] * 10 ** 10 / 100 * maxDurability / durabilityEffectDivider;
            }
            return (bltAttribute.pricePerRarity[rarity] + bltAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * bltAttribute.pricePerLevel) * multiplierPerGrade[grade] * 10 ** 10 * maxDurability / durabilityEffectDivider;
        } else if (_tokenType == 1) {
            return (csAttribute.pricePerRarity[rarity] + csAttribute.pricePerAdjective[adjective]) * (100000 + (level - 1) * csAttribute.pricePerLevel) * multiplierPerGrade[grade] / DECIMAL_FACTOR / DECIMAL_FACTOR / 100 * 10 ** 18 * maxDurability / durabilityEffectDivider;
        } else {
            return 0;
        }
    }

    function getMultipleRequiredPrice(uint8 _tokenType, uint256 _tokenId, uint256 _increaseLevel) public view returns (uint256) {
        (, uint8 maxDurability, ,uint8 adjective, uint8 rarity, uint8 grade) = blastEquipmentNFT.getStaticAttributes(_tokenId);
        (uint256 level, , , , ,) = blastEquipmentNFT.getAttributes(_tokenId);

        uint256 totalPrice;
        for (uint256 i = 0; i < _increaseLevel; i++) {
            totalPrice += getPrice(_tokenType, maxDurability, adjective, rarity, grade, level + i);
        }

        return totalPrice;
    }
}

