contracts/RentableNFT.sol.content:
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "./IERC4907.sol";
import "@openzeppelin/contracts/utils/Strings.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import {Errors} from "./libraries/Errors.sol";

contract RentableNFT is ERC721, IERC4907, Ownable, AccessControl {
    using Strings for uint256;

    uint256 private totalSupply;
    mapping(uint256 => uint256) public rentalPrices;
    mapping(uint256 => uint64) public rentalLengths;
    mapping(uint256 => uint64) public rentalStarts;
    mapping(uint256 => bool) public rentalAvailabilities;
    string private baseURI;

    struct UserInfo {
        address user; // address of user role
        uint64 expires; // unix timestamp, user expires
    }
    mapping(uint256 => UserInfo) internal _users;

    constructor(string memory _baseURI) ERC721("RentableNFT", "RNFT") {
        baseURI = _baseURI;
    }

    function mint(address to) external onlyOwner {
        require(totalSupply < 888, "Maximum token supply reached");

        totalSupply++;
        _safeMint(to, totalSupply);
    }

    function setRentalDetails(
        uint256 tokenId,
        uint256 price,
        uint64 rentalLength
    ) external {
        require(
            msg.sender == ownerOf(tokenId),
            "Only the token owner can set the rental details"
        );
        require(
            rentalStarts[tokenId] == 0,
            "Can only set rental details when token is not rented out"
        );
        rentalPrices[tokenId] = price;
        rentalLengths[tokenId] = rentalLength;
    }

    function setAvailableForRent(
        uint256 tokenId,
        bool rentalAvailability
    ) external {
        require(
            msg.sender == ownerOf(tokenId),
            "Only the token owner can set the rental details"
        );
        rentalAvailabilities[tokenId] = rentalAvailability;
    }

    function endRental(uint256 tokenId) external {
        require(
            msg.sender == ownerOf(tokenId),
            "Only the token owner can end the rental"
        );
        require(rentalStarts[tokenId] > 0, "Rental has not started");
        require(
            block.timestamp >= this.userExpires(tokenId),
            "Rental time is not over yet"
        );

        (bool sent, ) = payable(ownerOf(tokenId)).call{
            value: rentalPrices[tokenId]
        }("");
        require(sent, "Fail to send MATIC to the NFT owner");

        setUser(tokenId, address(0), 0);
        rentalStarts[tokenId] = 0;
    }

    function rentalInfo(
        uint256 tokenId
    ) external view returns (uint256, uint256, uint256, bool, uint64) {
        return (
            rentalPrices[tokenId],
            rentalLengths[tokenId],
            rentalStarts[tokenId],
            rentalAvailabilities[tokenId],
            uint64(block.timestamp)
        );
    }

    function rent(uint256 tokenId) external payable {
        require(rentalPrices[tokenId] > 0, "Rental price not set");
        require(rentalStarts[tokenId] == 0, "Token already rented");
        require(rentalAvailabilities[tokenId] == true, "Not available to rent");

        uint256 rentalPrice = rentalPrices[tokenId];
        require(msg.value == rentalPrice, "Incorrect rental price");

        rentalStarts[tokenId] = uint64(block.timestamp);
        setUser(
            tokenId,
            msg.sender,
            rentalStarts[tokenId] + rentalLengths[tokenId]
        );
    }

    /// @notice Set BaseURI for token metadata URI
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setBaseURI(
        string memory _baseURI
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = _baseURI;
    }

    function supportsInterface(
        bytes4 interfaceId
    ) public view virtual override(AccessControl, ERC721) returns (bool) {
        return
            interfaceId == type(IERC4907).interfaceId ||
            super.supportsInterface(interfaceId);
    }

    function getUri(
        uint256 tokenId
    ) public view virtual returns (string memory) {
        require(_exists(tokenId), "URI query for nonexistent token");

        return string(abi.encodePacked(baseURI, tokenId.toString()));
    }

    /// @notice set the user and expires of a NFT
    /// @dev The zero address indicates there is no user
    /// Throws if `tokenId` is not valid NFT
    /// @param user  The new user of the NFT
    /// @param expires  UNIX timestamp, The new user could use the NFT before expires
    function setUser(
        uint256 tokenId,
        address user,
        uint64 expires
    ) public virtual {
        require(
            _isApprovedOrOwner(msg.sender, tokenId),
            "ERC721: transfer caller is not owner nor approved"
        );
        UserInfo storage info = _users[tokenId];
        info.user = user;
        info.expires = expires;
        emit UpdateUser(tokenId, user, expires);
    }

    /// @notice Get the user address of an NFT
    /// @dev The zero address indicates that there is no user or the user is expired
    /// @param tokenId The NFT to get the user address for
    /// @return The user address for this NFT
    function userOf(uint256 tokenId) public view virtual returns (address) {
        if (uint256(_users[tokenId].expires) >= block.timestamp) {
            return _users[tokenId].user;
        } else {
            return address(0);
        }
    }

    /// @notice Get the user expires of an NFT
    /// @dev The zero value indicates that there is no user
    /// @param tokenId The NFT to get the user expires for
    /// @return The user expires for this NFT
    function userExpires(
        uint256 tokenId
    ) public view virtual returns (uint256) {
        return _users[tokenId].expires;
    }
}

contracts/IERC4907.sol.content:
// SPDX-License-Identifier: CC0-1.0

pragma solidity ^0.8.9;

interface IERC4907 {
    // Logged when the user of a token assigns a new user or updates expires
    /// @notice Emitted when the `user` of an NFT or the `expires` of the `user` is changed
    /// The zero address for user indicates that there is no user address
    event UpdateUser(
        uint256 indexed tokenId,
        address indexed user,
        uint64 expires
    );

    /// @notice set the user and expires of a NFT
    /// @dev The zero address indicates there is no user
    /// Throws if `tokenId` is not valid NFT
    /// @param user  The new user of the NFT
    /// @param expires  UNIX timestamp, The new user could use the NFT before expires
    function setUser(uint256 tokenId, address user, uint64 expires) external;

    /// @notice Get the user address of an NFT
    /// @dev The zero address indicates that there is no user or the user is expired
    /// @param tokenId The NFT to get the user address for
    /// @return The user address for this NFT
    function userOf(uint256 tokenId) external view returns (address);

    /// @notice Get the user expires of an NFT
    /// @dev The zero value indicates that there is no user
    /// @param tokenId The NFT to get the user expires for
    /// @return The user expires for this NFT
    function userExpires(uint256 tokenId) external view returns (uint256);
}

contracts/BlastLootBox.sol.content:
// SPDX-License-Identifier: MIT

pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import "@openzeppelin/contracts/token/ERC721/utils/ERC721Holder.sol";
import "@openzeppelin/contracts/token/ERC721/extensions/ERC721URIStorage.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/access/AccessControl.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "./interfaces/IBlastLootbox.sol";
import "./interfaces/IBlastEquipmentNFT.sol";
import { Errors } from "./libraries/Errors.sol";

/// @title Blast LootBox NFT
/// @dev BlastLootBox ERC721 token
contract BlastLootBox is
    IBlastLootbox,
    ERC721,
    ERC721URIStorage,
    ERC721Holder,
    Pausable,
    AccessControl
{
    using Counters for Counters.Counter;

    struct LootBox {
        uint256 token0;
        uint256 token1;
        uint256 token2;
    }

    bytes32 public constant GAME_ROLE = keccak256("GAME_ROLE");

    Counters.Counter public _tokenIdCounter;
    mapping(uint256 => LootBox) private lootboxDetails;
    mapping(uint256 => uint8) private tokenTypes;
    IBlastEquipmentNFT public blastEquipmentNFT;
    bool public openAvailable;

    /// @param name Name of the contract
    /// @param symbol Symbol of the contract
    constructor(
        string memory name,
        string memory symbol,
        IBlastEquipmentNFT _blastEquipmentNFT
    ) ERC721(name, symbol) {
        require(address(_blastEquipmentNFT) != address(0), Errors.NO_ZERO_ADDRESS);
        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(GAME_ROLE, _msgSender());
        blastEquipmentNFT = _blastEquipmentNFT;
    }

    /// @notice Creates a new token for `_to`. Its token ID will be automatically
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    /// _tokenType should be 1 or 2 (In case of 1, it's normal box. In case of 2, it's gw box)
    function safeMint(
        address[] calldata _to,
        string[] calldata _uri,
        LootBox[] calldata _eqtIds,
        uint8 _tokenType
    ) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_to.length == _uri.length && _to.length == _eqtIds.length, Errors.INVALID_PARAM);
        require(_tokenType == 1 || _tokenType == 2, Errors.INVALID_PARAM);

        for (uint256 i = 0; i < _to.length; i++) {
            uint256 tokenId = _tokenIdCounter.current();
            _tokenIdCounter.increment();
            lootboxDetails[tokenId] = _eqtIds[i];
            tokenTypes[tokenId] = _tokenType;
            _mint(_to[i], tokenId);
            _setTokenURI(tokenId, _uri[i]);
        }
    }

    function open(uint256 _tokenId) external {
        require(_exists(_tokenId), Errors.NOT_EXIST_TOKEN_ID);
        require(_msgSender() == ownerOf(_tokenId), Errors.NOT_OWNER);
        require(openAvailable, Errors.NOT_AVAILABLE_TO_OPEN);

        _open(_tokenId, _msgSender());
    }

    function openTo(uint256 _tokenId, address _to)
        external
        onlyRole(GAME_ROLE)
    {
        require(_exists(_tokenId), Errors.NOT_EXIST_TOKEN_ID);
        require(_to == ownerOf(_tokenId), Errors.NOT_OWNER);
        require(openAvailable, Errors.NOT_AVAILABLE_TO_OPEN);

        _open(_tokenId, _to);
    }

    function _open(uint256 _tokenId, address _to) internal {
        LootBox memory _eqtIds = lootboxDetails[_tokenId];

        blastEquipmentNFT.transferFrom(address(this), _to, _eqtIds.token0);
        blastEquipmentNFT.transferFrom(address(this), _to, _eqtIds.token1);
        blastEquipmentNFT.transferFrom(address(this), _to, _eqtIds.token2);

        blastEquipmentNFT.revealRealTokenURI(_eqtIds.token0);
        blastEquipmentNFT.revealRealTokenURI(_eqtIds.token1);
        blastEquipmentNFT.revealRealTokenURI(_eqtIds.token2);

        emit Open(_tokenId, _eqtIds.token0, _eqtIds.token1, _eqtIds.token2);
        _burn(_tokenId);
    }

    /// @notice Get Token Type. (GWB or NB)
    /// @dev Returned value should be 1 or 2
    /// @param _tokenId Token ID.
    function getTokenType(uint256 _tokenId)
        external
        view
        override
        returns (uint8)
    {
        return tokenTypes[_tokenId];
    }

    function setOpenAvailableStatus(bool _status)
        public
        onlyRole(DEFAULT_ADMIN_ROLE)
    {
        openAvailable = _status;
    }

    /// @notice Unpauses all token transfers.
    /// @dev The caller must be the Owner (or have approval) of the Token.
    /// @param tokenId Token ID.
    function _burn(uint256 tokenId)
        internal
        override(ERC721, ERC721URIStorage)
    {
        super._burn(tokenId);
    }

    /// @notice Returns the TokenURI.
    /// @param tokenId Token ID.
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721, ERC721URIStorage)
        returns (string memory)
    {
        return super.tokenURI(tokenId);
    }

    // @notice Pauses/Unpauses the contract
    // @dev While paused, actions are not allowed
    // @param stop whether to pause or unpause the contract.
    function pause(bool stop) external onlyRole(DEFAULT_ADMIN_ROLE) {
        if (stop) {
            _pause();
        } else {
            _unpause();
        }
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId
    ) internal virtual override whenNotPaused {
        super._beforeTokenTransfer(from, to, tokenId);
    }

    /// @dev See {IERC165-supportsInterface}.
    /// @param interfaceId Interface ID.
    function supportsInterface(bytes4 interfaceId)
        public
        view
        virtual
        override(AccessControl, IERC165, ERC721)
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }
}

