contracts/Utilities/Repairing.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@prb/math/contracts/PRBMathUD60x18.sol";
import "./../interfaces/IBlastEquipmentNFT.sol";
import "./Utility.sol";

contract Repairing is Utility {
    using SafeERC20 for IERC20;
    using PRBMathUD60x18 for uint256;

    uint256 private basePowerForCS = 2500; // 2.5
    uint256 private basePowerForBLST = 2025; // 2.025
    uint256 private basePriceForCS = 20000; // 20
    uint256 private basePriceForBLST = 50; // 0.05
    uint256 private basePriceForMATIC = 5; // 0.05
    uint16 private significanceK = 200; // DECIMAL_FACTOR 100

    event Repaired(uint256 tokenId, uint256 repairTimestamp);

    constructor(
        IBlastEquipmentNFT _blastEquipmentNFT,
        IERC20 _blastToken,
        ERC20Burnable _csToken,
        address _treasuryAddress,
        address _companyAddress
    ) Utility(_blastEquipmentNFT, _blastToken, _csToken, _treasuryAddress, _companyAddress) {
    }

    function getRepairPrice(uint256 _tokenId) public view returns (uint256) {
        (, uint256 durabilityRestored, uint256 durabilityPoint, , , ) = blastEquipmentNFT.getAttributes(_tokenId);
        uint256 temp = ((durabilityRestored * significanceK + DECIMAL_FACTOR) * durabilityPoint) * 10 ** 18 / DECIMAL_FACTOR;
        if (temp == 0) {
            return 0;
        }
        return PRBMathUD60x18.exp2(PRBMathUD60x18.div(PRBMathUD60x18.mul(PRBMathUD60x18.log2(temp), basePowerForCS), DECIMAL_FACTOR)) * basePriceForCS / DECIMAL_FACTOR;
    }

    function getRepairPriceBLST(uint256 _tokenId) public view returns (uint256) {
        (, uint256 durabilityRestored, uint256 durabilityPoint, , , ) = blastEquipmentNFT.getAttributes(_tokenId);
        if ((durabilityRestored + durabilityPoint) > 6) {
            uint256 temp = ((durabilityRestored + 1) * durabilityPoint);
            if (temp == 0) {
                return 0;
            }
            return PRBMathUD60x18.exp2(PRBMathUD60x18.div(PRBMathUD60x18.mul(PRBMathUD60x18.log2(temp * 10 ** 18), basePowerForBLST), DECIMAL_FACTOR)) * (isUsingMatic ? basePriceForMATIC : basePriceForBLST) / DECIMAL_FACTOR;
        }
        return 0;
    }

    /// @notice Set Base Power for CS and BLST. It will affect to calculate repair price for CS & BLST
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setBasePower(uint256 _basePowerForCS, uint256 _basePowerForBLST) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_basePowerForCS > 0, Errors.NO_ZERO_VALUE);
        require(_basePowerForBLST > 0, Errors.NO_ZERO_VALUE);

        basePowerForCS = _basePowerForCS;
        basePowerForBLST = _basePowerForBLST;
    }

    /// @notice Set significanceK value
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setSignificanceK(uint16 _significanceK) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_significanceK > 0, Errors.NO_ZERO_VALUE);
        significanceK = _significanceK;
    }

    /// @notice Set Base Price for CS and BLST. It will affect to calculate repair price for CS & BLST
    /// @dev The caller must have the `DEFAULT_ADMIN_ROLE`.
    function setBasePrice(uint256 _basePriceForCS, uint256 _basePriceForBLST, uint256 _basePriceForMATIC) external onlyRole(DEFAULT_ADMIN_ROLE) {
        require(_basePriceForCS > 0, Errors.NO_ZERO_VALUE);
        require(_basePriceForBLST > 0, Errors.NO_ZERO_VALUE);
        require(_basePriceForMATIC > 0, Errors.NO_ZERO_VALUE);

        basePriceForCS = _basePriceForCS;
        basePriceForBLST = _basePriceForBLST;
        basePriceForMATIC = _basePriceForMATIC;
    }

    function repair(
        uint256 _tokenId
    ) external nonReentrant whenNotPaused {
        require(!isUsingMatic, Errors.USING_MATIC_NOW);
        require(blastEquipmentNFT.ownerOf(_tokenId) == _msgSender(), Errors.NOT_OWNER);
        (, uint256 durabilityRestored, uint256 durabilityPoints, , uint256 repairCount, ) = blastEquipmentNFT.getAttributes(_tokenId);
        if ((durabilityRestored + durabilityPoints) > 6) {
            uint256 blstPrice = getRepairPriceBLST(_tokenId);
            require(blstPrice > 0, Errors.INVALID_AMOUNT);

            // Safe TransferFrom from msgSender to treasury
            blastToken.safeTransferFrom(_msgSender(), treasuryAddress, blstPrice / 4);
            blastToken.safeTransferFrom(_msgSender(), companyAddress, (blstPrice - blstPrice / 4));
        } else {
            uint256 price = getRepairPrice(_tokenId);
            require(price > 0, Errors.INVALID_AMOUNT);

            // Burning CS token from msgSender
            csToken.burnFrom(_msgSender(), price);
        }

        blastEquipmentNFT.setRepairCount(_tokenId, repairCount + 1);

        emit Repaired(_tokenId, block.timestamp);
    }

    function repairUsingMatic(uint256 _tokenId) external payable nonReentrant whenNotPaused {
        require(isUsingMatic, Errors.NOT_USING_MATIC_NOW);
        require(blastEquipmentNFT.ownerOf(_tokenId) == _msgSender(), Errors.NOT_OWNER);

        uint256 durabilityRestored;
        uint256 durabilityPoints;
        uint256 repairCount;
        (, durabilityRestored, durabilityPoints, , repairCount, ) = blastEquipmentNFT.getAttributes(_tokenId);
        if ((durabilityRestored + durabilityPoints) > 6) {
            uint256 maticPrice = getRepairPriceBLST(_tokenId);
            require(maticPrice > 0, Errors.INVALID_AMOUNT);
            require(msg.value == maticPrice, Errors.INVALID_AMOUNT);

            // Safe TransferFrom from msgSender to treasury
            (bool sent1, ) = payable(treasuryAddress).call{value: maticPrice / 4}("");
            require(sent1, Errors.FAILED_TO_SEND_ETHER_TREASURY);
            (bool sent2, ) = payable(companyAddress).call{value: (maticPrice - maticPrice / 4)}("");
            require(sent2, Errors.FAILED_TO_SEND_ETHER_COMPANY);
        } else {
            uint256 price = getRepairPrice(_tokenId);
            require(price > 0, Errors.INVALID_AMOUNT);

            // Burning CS token from msgSender
            csToken.burnFrom(_msgSender(), price);
        }

        blastEquipmentNFT.setRepairCount(_tokenId, repairCount + 1);

        emit Repaired(_tokenId, block.timestamp);
    }
}

contracts/Marketplace.sol.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.9;

import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import { Errors } from "./libraries/Errors.sol";

struct Listing {
  address owner;
  bool isActive;
  uint256 tokenId;
  uint256 price;
  IERC20 tokenAddress;
}

/// @title Blast Royale Token - $BLT
/// @dev Based on OpenZeppelin Contracts.
contract Marketplace is ReentrancyGuard, Ownable, Pausable {
  using SafeERC20 for IERC20;

  uint public constant DECIMAL_FACTOR = 100_00;

  uint256 public listingCount;
  uint256 public activeListingCount;
  uint256 public fee1;
  address public treasury1;
  uint256 public fee2;
  address public treasury2;
  bool public isUsingMatic;

  mapping (address => bool) public whitelistedTokens;
  mapping (uint256 => Listing) public listings;
  IERC721 public erc721Contract;

  /// @notice Event Listed
  event ItemListed(
    uint256 listingId,
    uint256 tokenId,
    address seller,
    uint256 price,
    address payTokenAddress
  );

  /// @notice Event Delisted
  event ItemDelisted(
    uint256 listingId,
    uint256 tokenId,
    address seller
  );

  /// @notice EventItem Sold
  event ItemSold(
    uint256 listingId,
    uint256 tokenId,
    address seller,
    address buyer,
    uint256 price,
    uint256 fee1,
    uint256 fee2
  );

  /// @notice Event Fee changed
  event FeesChanged(
    uint256 fee1,
    address treasury1,
    uint256 fee2,
    address treasury2,
    address changedBy
  );

  event WhitelistAdded(address[] whitelists);

  event WhitelistRemoved(address[] whitelists);

  /// @notice Token constructor
  /// @dev Setup the two contracts it will interact with : ERC721 and ERC20
  /// @param erc721Address Address of the NFT Contract.
  constructor(IERC721 erc721Address) {
    require(address(erc721Address) != address(0), Errors.NO_ZERO_ADDRESS);
    erc721Contract = erc721Address;
  }

  /// @notice add a Listing to the Marketplace
  /// @dev Creates a new entry for a Listing object and transfers the Token to the contract
  /// @param tokenId NFT TokenId.
  /// @param price Price in NFTs.
  function addListing(uint256 tokenId, uint256 price, IERC20 payTokenAddress) public nonReentrant whenNotPaused
  {
    require(price != 0, Errors.NO_ZERO_VALUE);
    if (address(payTokenAddress) != address(0)) {
      require(whitelistedTokens[address(payTokenAddress)], Errors.TOKEN_NOT_WHITELISTED);
    }

    uint256 listingId = listingCount;
    listings[listingId] = Listing({
      owner: _msgSender(),
      isActive: true,
      tokenId: tokenId,
      price: price,
      tokenAddress: payTokenAddress
    });
    listingCount = listingCount + 1;
    activeListingCount = activeListingCount + 1;
    erc721Contract.transferFrom(
      _msgSender(),
      address(this),
      tokenId
    );

    emit ItemListed(listingId, tokenId, _msgSender(), price, address(payTokenAddress));
  }

  /// @notice Remove a Listing from the Marketplace
  /// @dev Marks Listing as not active object and transfers the Token back
  /// @param listingId NFT Listing Id.
  function removeListing(uint256 listingId) public nonReentrant
  {
    Listing storage listing = listings[listingId];
    require(listing.owner == _msgSender() || owner() == _msgSender(), Errors.NOT_OWNER);
    require(listing.isActive, Errors.LISTING_IS_NOT_ACTIVED);
    listing.isActive = false;
    erc721Contract.transferFrom(
      address(this),
      listing.owner,
      listing.tokenId
    );
    activeListingCount = activeListingCount - 1;
    emit ItemDelisted(listingId, listing.tokenId, listing.owner);
  }

  /// @notice Buys a listed NFT
  /// @dev Trabsfers both the ERC20 token (price) and the NFT.
  /// @param listingId NFT Listing Id.
  function buy(uint256 listingId) public payable nonReentrant whenNotPaused
  {
    require(listings[listingId].isActive, Errors.LISTING_IS_NOT_ACTIVED);

    listings[listingId].isActive = false;
    IERC20 payTokenAddress = listings[listingId].tokenAddress;
    uint listedPrice = listings[listingId].price;
    uint256 buyingFee1 = (fee1 * listedPrice / DECIMAL_FACTOR);
    uint256 buyingFee2 = (fee2 * listedPrice / DECIMAL_FACTOR);

    if (isUsingMatic) {
      require(msg.value == listedPrice, Errors.INVALID_AMOUNT);
      if (buyingFee1 > 0) {
        (bool sent1, ) = payable(treasury1).call{value: buyingFee1}("");
        require(sent1, Errors.FAILED_TO_SEND_ETHER_TREASURY);
      }
      if (buyingFee2 > 0) {
        (bool sent2, ) = payable(treasury2).call{value: buyingFee2}("");
        require(sent2, Errors.FAILED_TO_SEND_ETHER_COMPANY);
      }
      (bool sent, ) = payable(listings[listingId].owner).call{value: listedPrice - buyingFee1 - buyingFee2}("");
      require(sent, Errors.FAILED_TO_SEND_ETHER_USER);
    } else {
      require(msg.value == 0, Errors.INVALID_AMOUNT);
      if (buyingFee1 > 0) {
        payTokenAddress.safeTransferFrom(
          _msgSender(),
          treasury1,
          buyingFee1
        );
      }
      if (buyingFee2 > 0) {
        payTokenAddress.safeTransferFrom(
          _msgSender(),
          treasury2,
          buyingFee2
        );
      }
      payTokenAddress.safeTransferFrom(
        _msgSender(),
        listings[listingId].owner,
        listedPrice - buyingFee1 - buyingFee2
      );
    }
    erc721Contract.transferFrom(
      address(this),
      _msgSender(),
      listings[listingId].tokenId
    );
    activeListingCount = activeListingCount - 1;

    emit ItemSold(
      listingId,
      listings[listingId].tokenId,
      listings[listingId].owner,
      _msgSender(),
      listedPrice,
      buyingFee1,
      buyingFee2
    );
  }

  /// @notice Sets a new Fee
  /// @param _fee1 new Fee1.
  /// @param _treasury1 New treasury1 address.
  /// @param _fee2 new Fee2.
  /// @param _treasury2 New treasury2 address.
  function setFee(uint256 _fee1, address _treasury1, uint256 _fee2, address _treasury2) public onlyOwner
  {
    require(_fee1 + _fee2 < DECIMAL_FACTOR, Errors.INVALID_PARAM);
    require(_treasury1 != address(0), Errors.NO_ZERO_ADDRESS);
    require(_treasury2 != address(0), Errors.NO_ZERO_ADDRESS);

    fee1 = _fee1;
    treasury1 = _treasury1;
    fee2 = _fee2;
    treasury2 = _treasury2;

    emit FeesChanged(
      fee1,
      treasury1,
      fee2,
      treasury2,
      _msgSender()
    );
  }

  function setWhitelistTokens(address[] calldata _whitelist) external onlyOwner {
    for (uint i = 0; i < _whitelist.length; i++) {
      require(_whitelist[i] != address(0), Errors.NO_ZERO_ADDRESS);
      whitelistedTokens[_whitelist[i]] = true;
    }

    emit WhitelistAdded(_whitelist);
  }

  function removeWhitelistTokens(address[] calldata _whitelist) external onlyOwner {
    for (uint i = 0; i < _whitelist.length; i++) {
      require(_whitelist[i] != address(0), Errors.NO_ZERO_ADDRESS);
      whitelistedTokens[_whitelist[i]] = false;
    }

    emit WhitelistRemoved(_whitelist);
  }

  function flipIsUsingMatic() external onlyOwner {
    isUsingMatic = !isUsingMatic;
  }

  // @notice Pauses/Unpauses the contract
  // @dev While paused, addListing, and buy are not allowed
  // @param stop whether to pause or unpause the contract.
  function pause(bool stop) external onlyOwner {
    if (stop) {
      _pause();
    } else {
      _unpause();
    }
  }
}

